CREATE TABLE IF NOT EXISTS film (
    `id` VARCHAR(10) CHARACTER SET utf8,
    `judul` VARCHAR(62) CHARACTER SET utf8,
    `usia` VARCHAR(5) CHARACTER SET utf8,
    `tahun` INT,
    `durasi` VARCHAR(3) CHARACTER SET utf8,
    `type` VARCHAR(12) CHARACTER SET utf8,
    `negara` VARCHAR(14) CHARACTER SET utf8,
    `plot` VARCHAR(232) CHARACTER SET utf8,
    `ringkasan` VARCHAR(1748) CHARACTER SET utf8,
    `rating` NUMERIC(2, 1),
    `genres` VARCHAR(27) CHARACTER SET utf8,
    `pemain` VARCHAR(443) CHARACTER SET utf8,
    `image_url` VARCHAR(154) CHARACTER SET utf8
);
